import React, { Component } from 'react'
import axios from 'axios'
import './style.css'

export class Fulldata extends Component {
    constructor(props) {
        super(props)

        this.state = {
             fulldata: []
        }
    }


    componentDidMount() {
        this.getData();
    }
    componentWillReceiveProps(){

    }

    getData =()=>{
        axios.get('samplejson/customer'+this.props.val+'.json').then((data)=>{
            console.log(this.state)
            let {fulldata} = this.state
            fulldata = data.data
            this.setState({fulldata:fulldata},()=>{
            })
        })
    }

    render() {
        return (
            <div>
                <h3>{this.props.val}</h3>
                {console.log(this.state.fulldata)}
                <p>{this.state.fulldata.name}</p>
                <p>{this.state.fulldata.email}</p>
                <p>{this.state.fulldata.phone}</p>
                <p>{this.state.fulldata.city}</p>
                <p>{this.state.fulldata.state}</p>
                <p>{this.state.fulldata.country}</p>
                <p>{this.state.fulldata.organization}</p>
                <p>{this.state.fulldata.jobProfile}</p>
                <p>{this.state.fulldata.additionalInfo}</p>






            </div>
        )
    }
}

export default Fulldata
