export class Customer extends Component {
    constructor(props) {
        super(props)

        this.state = {
             selectedCustomer : 1 ,
        }
    }

    componentDidMount() {
        this.getData();
    }

    getData(){
        axios.get('samplejson/customerlist.json').then((data)=>{
            this.setState({customerlist: data.data})
        })
    }
    render() {
        console.log(this.state.customerlist)
